import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-post-listing',
  templateUrl: './post-listing.component.html',
  styleUrls: ['./post-listing.component.css']
})
export class PostListingComponent {
  public createForm: FormGroup;
  public showInput: boolean = false;
  public isDealer: boolean = false;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) { }

  ngOnInit() {
    this.initializeFormControls();
  }

  initializeFormControls() {
    this.createForm = new FormGroup({
      year: new FormControl(),
      make: new FormControl(),
      model: new FormControl(),
      comments: new FormControl(),
      driveAwayPrice: new FormControl(),
      excludingGovernmentCharges: new FormControl(),
      nonDealerDetails: new FormGroup({
        name: new FormControl(),
        contactNumber: new FormControl()
      }),
      dealerDetails: new FormGroup({
        name: new FormControl(),
        contactNumber: new FormControl(),
        email: new FormControl(),
        abn: new FormControl()
      })
    });
  }

  updateListing() {
    if (this.createForm.valid) {
      const updatedListing: CarListing = {
        comments: this.createForm.get('comments').value,
        driveAwayPrice: this.createForm.get('driveAwayPrice').value,
        excludingGovernmentCharges: this.createForm.get('excludingGovernmentCharges').value
      };

      this.http.put(this.baseUrl + 'api/carlisting/update?id=2', updatedListing).subscribe(
        () => {
          // Update successful
          console.log('Listing updated successfully!');
        },
        error => {
          console.error('Error updating listing:', error);
          // Handle the error case if needed
        }
      );
    }
  }
}

interface CarListing {
  year?: string;
  make?: string;
  model?: string;
  comments: string;
  driveAwayPrice?: number;
  excludingGovernmentCharges?: number;
  nonDealerDetails?: {
    name: string;
    contactNumber: string;
  }
  dealerDetails?: {
    name: string;
    email: string;
    contactNumber: string;
    abn: string;
  }
}
<h1>Post a Listing</h1>


<div>
  <form [formGroup]="createForm" (ngSubmit)="updateListing()">
    <dt>Year</dt>
    <input type="text" formControlName="year"/>

    <dt>Make</dt>
    <input type="text" formControlName="make"/>

    <dt>Model</dt>
    <input type="text" formControlName="model"/>

    <dt>Comments</dt>
    <textarea formControlName="comments"></textarea>

    <dt>Price</dt>
    <label>
      <input type="radio" value="yes" (change)="showInput = true"> DAP
    </label>
    <label>
      <input type="radio" value="no" (change)="showInput = false"> EGC
    </label>

    <dt>Drive Away Price (DAP)</dt>
    <input *ngIf="showInput" formControlName="driveAwayPrice"/>

    <dt>Excluding Government Charges (EGC)</dt>
    <input *ngIf="!showInput" formControlName="driveAwayPrice"/>

    <label>
      <input type="radio" value="non-dealer" (change)="isDealer = false"> Non-dealer Vehicle
    </label>
    <label>
      <input type="radio" value="dealer" (change)="isDealer = true"> Dealer Vehicle
    </label>

    <div *ngIf="!isDealer; else elseBlock" [formGroup]="nonDealerDetails">
      <dt>Name</dt>
      <input type="text" formControlName="name" required/>

      <dt>Phone</dt>
      <input type="text" formControlName="contactNumber" required/>
    </div>

    <ng-template #elseBlock>
      <div [formGroup]="dealerDetails">
        <dt>Name</dt>
        <input type="text" formControlName="name" required/>

        <dt>Phone</dt>
        <input type="text" formControlName="contactNumber" required/>

        <dt>Email</dt>
        <input type="email" formControlName="email" required/>

        <dt>ABN</dt>
        <input type="text" formControlName="email" required/>
      </div>
    </ng-template>

    <input type="submit" value="submit"/>

  </form>
</div>
