import { HttpClient } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-edit-listing',
  templateUrl: './edit-listing.component.html',
  styleUrls: ['./edit-listing.component.css']
})
export class EditListingComponent implements OnInit {
  public listingToEdit: CarListing = { year: '', make: '', model: '', comments: '' };
  public editForm: FormGroup;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) { }

  ngOnInit() {
    this.http.get<CarListing>(this.baseUrl + `api/carlisting/find?id=2`).subscribe(
      result => {
        this.listingToEdit = result;
        this.initializeFormControls();
      },
      error => {
        console.error(error);
        // Handle the error case if needed
      }
    );
  }

  initializeFormControls() {
    this.editForm = new FormGroup({
      comments: new FormControl(this.listingToEdit.comments),
      driveAwayPrice: new FormControl(this.listingToEdit.driveAwayPrice),
      excludingGovernmentCharges: new FormControl(this.listingToEdit.excludingGovernmentCharges)
    });
  }

  updateListing() {
    if (this.editForm.valid) {
      const updatedListing: CarListing = {
        year: this.listingToEdit.year,
        make: this.listingToEdit.make,
        model: this.listingToEdit.model,
        comments: this.editForm.get('comments').value,
        driveAwayPrice: this.editForm.get('driveAwayPrice').value,
        excludingGovernmentCharges: this.editForm.get('excludingGovernmentCharges').value
      };

      this.http.put(this.baseUrl + 'api/carlisting/update?id=2', updatedListing).subscribe(
        () => {
          // Update successful
          console.log('Listing updated successfully!');
        },
        error => {
          console.error('Error updating listing:', error);
          // Handle the error case if needed
        }
      );
    }
  }
}

interface CarListing {
  year: string;
  make: string;
  model: string;
  comments: string;
  driveAwayPrice?: number;
  excludingGovernmentCharges?: number;
  nonDealerDetails?: {
    name: string;
    contactNumber: string;
  }
  dealerDetails?: {
    name: string;
    email: string;
    contactNumber: string;
    abn: string;
  }
}
<h1>Edit a Listing</h1>


<div>
  <form [formGroup]="editForm" (ngSubmit)="updateListing()">
    <dt>Year</dt>
    <dd>{{ listingToEdit.year }}</dd>

    <dt>Make</dt>
    <dd>{{ listingToEdit.make }}</dd>

    <dt>Model</dt>
    <dd>{{ listingToEdit.model }}</dd>

    <dt>Comments</dt>
    <textarea formControlName="comments"> {{ listingToEdit.comments }}</textarea>

    <dt>Drive Away Price (DAP)</dt>
    <input type="text" formControlName="driveAwayPrice"/>

    <dt>Excluding Government Charges (EGC)</dt>
    <input type="text" formControlName="excludingGovernmentCharges"/>

    <div *ngIf="listingToEdit.dealerDetails === null; else elseBlock">
      <dt>Name</dt>
      <dd>{{ listingToEdit.nonDealerDetails?.name }}</dd>

      <dt>Phone</dt>
      <dd>{{ listingToEdit.nonDealerDetails?.contactNumber }}</dd>
    </div>

    <ng-template #elseBlock>
      <div>
        <dt>Name</dt>
        <dd>{{ listingToEdit.dealerDetails?.name }}</dd>

        <dt>Phone</dt>
        <dd>{{ listingToEdit.dealerDetails?.contactNumber }}</dd>

        <dt>Email</dt>
        <dd>{{ listingToEdit.dealerDetails?.email }}</dd>

        <dt>ABN</dt>
        <dd>{{ listingToEdit.dealerDetails?.abn }}</dd>
      </div>
    </ng-template>

    <input type="submit" value="submit"/>

  </form>
</div>
