import { HttpClient } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-find-listing',
  templateUrl: './find-listing.component.html',
  styleUrls: ['./find-listing.component.css']
})
export class FindListingComponent {
  public listing: CarListing;
  public searchForm: FormGroup;

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) { }

  searchListing() {
    this.http.get<CarListing>(this.baseUrl + `api/carlisting/find?id=${this.searchForm.get('searchId').value}`).subscribe(
      result => {
        this.listing = result;
        this.initializeEditForm();
      },
      error => {
        console.error(error);
        // Handle the error case if needed
      }
    );
  }

  ngOnInit() {
    this.searchForm = new FormGroup({
      searchId: new FormControl(null)
    });
  }

  initializeSearchListing() {
    this.editForm = new FormGroup({
      comments: new FormControl(this.listingToEdit.comments),
      driveAwayPrice: new FormControl(this.listingToEdit.driveAwayPrice),
      excludingGovernmentCharges: new FormControl(this.listingToEdit.excludingGovernmentCharges)
    });
  }

  updateListing() {
    if (this.editForm.valid) {
      const updatedListing: CarListing = {
        comments: this.editForm.get('comments').value,
        driveAwayPrice: this.editForm.get('driveAwayPrice').value,
        excludingGovernmentCharges: this.editForm.get('excludingGovernmentCharges').value
      };

      this.http.put(this.baseUrl + 'api/carlisting/update?id=2', updatedListing).subscribe(
        () => {
          // Update successful
          console.log('Listing updated successfully!');
        },
        error => {
          console.error('Error updating listing:', error);
          // Handle the error case if needed
        }
      );
    }
  }
}

interface CarListing {
  year?: string;
  make?: string;
  model?: string;
  comments: string;
  driveAwayPrice?: number;
  excludingGovernmentCharges?: number;
  nonDealerDetails?: {
    name: string;
    contactNumber: string;
  }
  dealerDetails?: {
    name: string;
    email: string;
    contactNumber: string;
    abn: string;
  }
}
<h1>Find a Listing</h1>


<div>
  <form [formGroup]="searchForm" (ngSubmit)="searchListing()" >
    <dt> Search by Listing Id </dt>
    <input type="text" formControlName="searchId"/>
    <input type="submit" value="search"/>
  </form>
    <dt>Year</dt>
    <dd>{{ listing.year }}</dd>

    <dt>Make</dt>
    <dd>{{ listing.make }}</dd>

    <dt>Model</dt>
    <dd>{{ listing.model }}</dd>

    <dt>Comments</dt>
    <textarea formControlName="comments"> {{ listing.comments }}</textarea>

    <dt>Drive Away Price (DAP)</dt>
    <input *ngIf="listing.driveAwayPrice !== 0" type="text" formControlName="driveAwayPrice"/>

    <dt>Excluding Government Charges (EGC)</dt>
    <input *ngIf="listing.excludingGovernmentCharges !== 0" type="text" formControlName="excludingGovernmentCharges"/>

    <div *ngIf="listing.dealerDetails === null; else elseBlock">
      <dt>Name</dt>
      <dd>{{ listing.nonDealerDetails?.name }}</dd>

      <dt>Phone</dt>
      <dd>{{ listing.nonDealerDetails?.contactNumber }}</dd>
    </div>

    <ng-template #elseBlock>
      <div>
        <dt>Name</dt>
        <dd>{{ listing.dealerDetails?.name }}</dd>

        <dt>Phone</dt>
        <dd>{{ listing.dealerDetails?.contactNumber }}</dd>

        <dt>Email</dt>
        <dd>{{ listing.dealerDetails?.email }}</dd>

        <dt>ABN</dt>
        <dd>{{ listing.dealerDetails?.abn }}</dd>
      </div>
    </ng-template>

    <input type="submit" value="submit"/>
</div>
