import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-edit-listing',
  templateUrl: './edit-listing.component.html',
  styleUrls: ['./edit-listing.component.css']
})
export class EditListingComponent {
  public listingToEdit: CarListing = { year: '', make: '', model: '', comments: '' };
  public editForm: FormGroup;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<CarListing>(baseUrl + `api/carlisting/find?id=2`).subscribe(result => {
      this.listingToEdit = result;
    }, error => console.error(error));
  }

  ngOnInit() {
    this.editForm = new FormGroup({
      comments: new FormControl(this.listingToEdit.comments),
      driveAwayPrice: new FormControl(this.listingToEdit.driveAwayPrice),
      excludingGovernmentCharges: new FormControl(this.listingToEdit.excludingGovernmentCharges)
    })
  }
}

interface CarListing {
  year: string;
  make: string;
  model: string;
  comments: string;
  driveAwayPrice?: number;
  excludingGovernmentCharges?: number;
  nonDealerDetails?: {
    name: string;
    contactNumber: string;
  }
  dealerDetails?: {
    name: string;
    email: string;
    contactNumber: string;
    abn: string;
  }
}
<h1>Edit a Listing</h1>


<div>
  <form [formGroup]="editForm">
    <dt>Year</dt>
    <dd>{{ listingToEdit.year }}</dd>

    <dt>Make</dt>
    <dd>{{ listingToEdit.make }}</dd>

    <dt>Model</dt>
    <dd>{{ listingToEdit.model }}</dd>

    <dt>Comments (editable)</dt>
    <input type="text" formControlName="comments"/>

    <dt>Drive Away Price (DAP) (editable)</dt>
    <input type="text" formControlName="driveAwayPrice"/>

    <dt>Excluding Government Charges (EGC) (editable)</dt>
    <input type="text" formControlName="excludingGovernmentCharges"/>

    <div *ngIf="listingToEdit.dealerDetails === null; else elseBlock">
      <dt>Name</dt>
      <dd>{{ listingToEdit.nonDealerDetails?.name }}</dd>

      <dt>Phone</dt>
      <dd>{{ listingToEdit.nonDealerDetails?.contactNumber }}</dd>
    </div>

    <ng-template #elseBlock>
      <div>
        <dt>Name</dt>
        <dd>{{ listingToEdit.dealerDetails?.name }}</dd>

        <dt>Phone</dt>
        <dd>{{ listingToEdit.dealerDetails?.contactNumber }}</dd>

        <dt>Email</dt>
        <dd>{{ listingToEdit.dealerDetails?.email }}</dd>

        <dt>ABN</dt>
        <dd>{{ listingToEdit.dealerDetails?.abn }}</dd>
      </div>
    </ng-template>


  </form>
</div>
