import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-post-listing',
  templateUrl: './post-listing.component.html',
  styleUrls: ['./post-listing.component.css']
})
export class PostListingComponent {
  public createForm: FormGroup;
  public nonDealerDetailsForm: FormGroup;
  public dealerDetailsForm: FormGroup;
  public priceType: string = 'dap';
  public dealerType: string = 'non-dealer';

  constructor(private http: HttpClient, @Inject('BASE_URL') private baseUrl: string) {
  }

  ngOnInit() {
    this.initializeFormControls();
  }

  initializeFormControls() {

    this.nonDealerDetailsForm = new FormGroup({
      name: new FormControl(),
      contactNumber: new FormControl()
    });

    this.dealerDetailsForm = new FormGroup({
      name: new FormControl(),
      contactNumber: new FormControl(),
      email: new FormControl(),
      abn: new FormControl()
    });

    this.createForm = new FormGroup({
      year: new FormControl(),
      make: new FormControl(),
      model: new FormControl(),
      comments: new FormControl(),
      driveAwayPrice: new FormControl(0),
      excludingGovernmentCharges: new FormControl(),
      nonDealerDetails: this.nonDealerDetailsForm,
      dealerDetails: this.dealerDetailsForm
    });
  }

  createListing() {
    if (this.createForm.valid) {
      const newListing: CarListing = {
        year: this.createForm.get('year').value,
        make: this.createForm.get('make').value,
        model: this.createForm.get('model').value,
        comments: this.createForm.get('comments').value,
        driveAwayPrice: this.createForm.get('driveAwayPrice').value ?? 0,
        excludingGovernmentCharges: this.createForm.get('excludingGovernmentCharges').value ?? 0,
        nonDealerDetails: {
          name: this.nonDealerDetailsForm.get('name').value,
          contactNumber: this.nonDealerDetailsForm.get('contactNumber').value
        },
        dealerDetails: {
          name: this.dealerDetailsForm.get('name').value,
          contactNumber: this.dealerDetailsForm.get('contactNumber').value,
          email: this.dealerDetailsForm.get('email').value,
          abn: this.dealerDetailsForm.get('abn').value
        },
      };

      console.log(newListing);

      this.http.post(this.baseUrl + 'api/carlisting/create', newListing).subscribe(
        () => {
          // Update successful
          console.log('Listing created successfully!');
        },
        error => {
          console.error('Error updating listing:', error);
          // Handle the error case if needed
        }
      );
    }
  }
}

interface CarListing {
  year?: string;
  make?: string;
  model?: string;
  comments: string;
  driveAwayPrice?: number;
  excludingGovernmentCharges?: number;
  nonDealerDetails?: {
    name: string;
    contactNumber: string;
  }
  dealerDetails?: {
    name: string;
    email: string;
    contactNumber: string;
    abn: string;
  }
}
<h1>Post a Listing</h1>


<div>
  <form [formGroup]="createForm" (ngSubmit)="createListing()">
    <dt>Year</dt>
    <input type="text" formControlName="year"/>

    <dt>Make</dt>
    <input type="text" formControlName="make"/>

    <dt>Model</dt>
    <input type="text" formControlName="model"/>

    <dt>Comments</dt>
    <textarea formControlName="comments"></textarea>

    <div>
      <dt>Price</dt>
      <label>
        <input type="radio" name="priceType" [(ngModel)]="priceType" [ngModelOptions]="{standalone: true}" value="dap" (change)="createForm.controls.markAsDirty(); createForm.controls.markAsTouched();" /> DAP
      </label>
      <label>
        <input type="radio" name="priceType" [(ngModel)]="priceType" [ngModelOptions]="{standalone: true}" value="egc" (change)="createForm.controls.markAsDirty(); createForm.controls.markAsTouched();" /> EGC
      </label>
    </div>

    <div>
      <dt *ngIf="priceType === 'dap'">Drive Away Price (DAP)</dt>
      <input type="text" *ngIf="priceType === 'dap'" formControlName="driveAwayPrice"/>

      <dt *ngIf="priceType === 'egc'">Excluding Government Charges (EGC)</dt>
      <input type="text" *ngIf="priceType === 'egc'" formControlName="excludingGovernmentCharges"/>
    </div>

    <div>
      <dt>Vehicle Type</dt>
      <label>
        <input type="radio" name="dealerType" [(ngModel)]="dealerType" [ngModelOptions]="{standalone: true}" value="non-dealer" (change)="createForm.controls.markAsDirty(); createForm.controls.markAsTouched();" /> Non-Dealer
      </label>
      <label>
        <input type="radio" name="dealerType" [(ngModel)]="dealerType" [ngModelOptions]="{standalone: true}" value="dealer" (change)="createForm.controls.markAsDirty(); createForm.controls.markAsTouched();" /> Dealer
      </label>
    </div>

    <div *ngIf="dealerType === 'non-dealer'; else elseBlock" [formGroup]="nonDealerDetailsForm">
      <dt>Name</dt>
      <input type="text" formControlName="name" required/>

      <dt>Phone</dt>
      <input type="text" formControlName="contactNumber" required/>
    </div>

    <ng-template #elseBlock>
      <div [formGroup]="dealerDetailsForm">
        <dt>Name</dt>
        <input type="text" formControlName="name" required/>

        <dt>Phone</dt>
        <input type="text" formControlName="contactNumber" required/>

        <dt>Email</dt>
        <input type="email" formControlName="email" required/>

        <dt>ABN</dt>
        <input type="text" formControlName="abn" required/>
      </div>
    </ng-template>

    <input type="submit" value="submit"/>

  </form>
</div>
